        // Constructor to initialize a node with data
        Node(int data) {
            this.data = data;
            this.next = null;
        }
    }

    private Node head;   // Reference to the first node in the list

    // Method to add a node at the beginning of the list
    public void addBeginning(int data) {
        Node newNode = new Node(data); // Create a new node with the given data
        newNode.next = head;           // Point the new node's next to the current head
        head = newNode;                // Update the head to the new node
    }

    // Method to add a node at the end of the list
    public void addEnd(int data) {
        Node newNode = new Node(data); // Create a new node with the given data

        if (head == null) {            // If the list is empty, set the new node as the head
            head = newNode;
        } else {
            Node current = head;       // Start from the head
            while (current.next != null) { // Traverse to the last node
                current = current.next;
            }
            current.next = newNode;    // Add the new node at the end
        }
    }

    // Method to add a node at a specific position in the list
    public void addAtPosition(int position, int data) {
        if (position < 0) {
            throw new IllegalArgumentException("Position cannot be negative.");
        }

        Node newNode = new Node(data); // Create a new node with the given data

        if (position == 0) {           // If position is 0, add at the beginning
            newNode.next = head;
            head = newNode;
            return;
        }

        Node current = head;          // Start from the head
        for (int i = 0; i < position - 1; i++) { // Traverse to the node before the desired position
            if (current == null) {
                throw new IndexOutOfBoundsException("Position exceeds the list size.");
            }
            current = current.next;
        }

        newNode.next = current.next;   // Insert the new node at the specified position
        current.next = newNode;
    }

    // Method to reverse the linked list
    public void reverse() {
        Node prev = null;              // Previous node, initially null
        Node current = head;          // Current node, starting from the head
        Node next = null;              // Next node, initially null

        while (current != null) {      // Traverse the list
            next = current.next;       // Store the next node
            current.next = prev;       // Reverse the current node's pointer
            prev = current;           // Move prev to the current node
            current = next;           // Move current to the next node
        }

        head = prev;                   // Update the head to the new first node
