```java
/**
 * This class implements a singly linked list with basic operations:
 * adding a node at the beginning, adding a node at the end, 
 * adding a node at a specific position, and reversing the linked list.
 */
public class LinkedList {
    private Node head; // The head node of the linked list

    // Inner class representing a node in the linked list
    class Node {
        int data; // Data contained in the node
        Node next; // Reference to the next node

        // Constructor to initialize a node with data
        Node(int data) {
            this.data = data;
            this.next = null; // Next is initially null
        }
    }

    /**
     * Adds a new node with the specified data at the beginning of the list.
     * 
     * @param data The data to be added to the new node.
     */
    public void addBeginning(int data) {
        Node newNode = new Node(data); // Create a new node
        newNode.next = head; // Set its next reference to the current head
        head = newNode; // Update head to point to the new node
    }

    /**
     * Adds a new node with the specified data at the end of the list.
     * 
     * @param data The data to be added to the new node.
     */
    public void addEnd(int data) {
        Node newNode = new Node(data); // Create a new node
        if (head == null) { // If the list is empty, set head to the new node
            head = newNode;
        } else {
            Node current = head; // Start from head
            while (current.next != null) { // Traverse to the last node
                current = current.next;
            }
            current.next = newNode; // Link the last node to the new node
        }
    }

    /**
     * Adds a new node with the specified data at a specific position in the list.
     * 
     * @param position The position at which to insert the new node (0-based index).
     * @param data The data to be added to the new node.
     */
    public void addAtPosition(int position, int data) {
        if (position < 0) {
            throw new IndexOutOfBoundsException("Position cannot be negative.");
        }
        Node newNode = new Node(data); // Create a new node
        if (position == 0) {
            addBeginning(data); // If position is 0, add at the beginning
            return;
        }
        Node current = head; // Start from head
        for (int i = 0; i < position - 1; i++) {
            if (current == null) {
                throw new IndexOutOfBoundsException("Position exceeds list length.");
            }
            current = current.next; // Traverse to the position before the desired position
        }
        newNode.next = current.next; // Link new node to the next node
        current.next = newNode; // Insert new node
    }

    /**
     * Reverses the linked list while maintaining the same memory references for nodes.
     */
    public void reverse() {
        Node previous = null; // Previous node reference
        Node currentNode = head; // Current node reference
        Node nextNode; // Next node reference

        // Traverse the list and reverse the links
        while (currentNode != null) {
            nextNode = currentNode.next; // Store next node
            currentNode.next = previous; // Reverse the link
            previous = currentNode; // Move previous to current
            currentNode = nextNode; // Move to the next node
        }
        head = previous; // Update head to the new front
    }

    /**
     * Prints the linked list in a readable format.
     */
    public void printList() {
        Node current = head; // Start from head
        while (current != null) {
            System.out.print(current.data + " -> "); // Print data
            current = current.next; // Move to the next node
        }
        System.out.println("null"); // Indicate the end of the list
    }

    // Main method to demonstrate the functionality of the LinkedList class
    public static void main(String[] args) {
        LinkedList list = new LinkedList();
        list.addBeginning(10);
