```java
public class LinkedList {
    // Head of the linked list
    private Node head;

    // Node class representing each element in the linked list
    private class Node {
        int data;        // Data held by the node
        Node next;      // Pointer to the next node

        // Constructor to initialize a new node with data
        Node(int data) {
            this.data = data;
            this.next = null; // Initialize next pointer to null
        }
    }

    // Method to add a new node at the beginning of the list
    public void addBeginning(int data) {
        Node newNode = new Node(data);  // Create a new node with the given data
        newNode.next = head;             // Point new node's next to the current head
        head = newNode;                  // Update head to the new node
    }

    // Method to add a new node at the end of the list
    public void addEnd(int data) {
        Node newNode = new Node(data);  // Create a new node
        if (head == null) {              // If the list is empty
            head = newNode;              // Set head to new node
        } else {
            Node current = head;          // Start from the head
            while (current.next != null) { // Traverse to the last node
                current = current.next;
            }
            current.next = newNode;       // Add new node at the end
        }
    }

    // Method to add a new node at a specific position in the list
    public void addAtPosition(int position, int data) {
        if (position < 0) {
            throw new IndexOutOfBoundsException("Position cannot be negative.");
        }

        Node newNode = new Node(data);   // Create a new node
        if (position == 0) {              // If the position is 0, add at the beginning
            addBeginning(data);
            return;
        }

        Node current = head;              // Start from the head
        for (int i = 0; i < position - 1; i++) {
            if (current == null) {
                throw new IndexOutOfBoundsException("Position exceeds list size.");
            }
            current = current.next;       // Move to the next node
        }
        newNode.next = current.next;      // Link the new node to the next node
        current.next = newNode;           // Link the previous node to the new node
    }

    // Method to reverse the linked list
    public void reverse() {
        Node previous = null;              // Initialize previous node as null
        Node currentNode = head;           // Start with the head of the list
        Node nextNode;                     // To temporarily hold the next node

        while (currentNode != null) {      // Traverse the list until the end
            nextNode = currentNode.next;    // Store the next node
            currentNode.next = previous;     // Reverse the link
            previous = currentNode;          // Move previous to current node
            currentNode = nextNode;          // Move to the next node
        }
        head = previous;                   // Update head to the new front of the list
    }

    // Optional: Method to print the linked list (for testing purposes)
    public void printList() {
        Node current = head;               // Start from the head
        while (current != null) {          // Traverse until the end
            System.out.print(current.data + " -> ");
            current = current.next;        // Move to the next node
        }
        System.out.println("null");        // Indicate end of the list
    }

    // Main method for testing the LinkedList implementation
    public static void main(String[] args) {
        LinkedList list = new LinkedList();
        list.addBeginning(10);
        list.addEnd(20);
        list.addAtPosition(1, 15);
        System.out.println("Original List:");
        list.printList(); // Output: 10 -> 15 -> 20 -> null

        list.reverse();
        System.out.println("Reversed List:");
        list.printList(); // Output: 20 -> 15 -> 10 -> null
    }
}
```

