```cpp
#include <afxwin.h>

class CMyFrame : public CFrameWnd
{
public:
    CMyFrame()
    {
        // Create a CEdit control and attach it to the frame
        m_edit.Create(WS_CHILD | WS_VISIBLE | WS_BORDER | ES_MULTILINE, 
                      CRect(0, 0, 200, 200), this, 1);
    }

protected:
    // CEdit control member
    CEdit m_edit;

    // Override OnSize to handle resizing of the CEdit control
    afx_msg void OnSize(UINT nType, int cx, int cy)
    {
        // Call the base class implementation
        CFrameWnd::OnSize(nType, cx, cy);

        // Resize the CEdit control to fill the client area
        if (m_edit.GetSafeHwnd()) // Ensure the control is created
        {
            m_edit.MoveWindow(0, 0, cx, cy);
        }
    }

    DECLARE_MESSAGE_MAP()
};

BEGIN_MESSAGE_MAP(CMyFrame, CFrameWnd)
    ON_WM_SIZE() // Map the WM_SIZE message to OnSize function
END_MESSAGE_MAP()

class CMyApp : public CWinApp
{
public:
    virtual BOOL InitInstance()
    {
        CMyFrame* pFrame = new CMyFrame();
        m_pMainWnd = pFrame;
        pFrame->ShowWindow(SW_SHOW);
        pFrame->UpdateWindow();
        return TRUE;
    }
};

CMyApp theApp;
```

