import java.util.Arrays;
import java.util.List;

/**
 * SearchEngine provides an efficient search mechanism for web application data.
 * Implements binary search algorithm for sorted collections with comprehensive error handling.
 */
public class SearchEngine {

    /**
     * Performs binary search on a sorted list of elements.
     * 
     * Time Complexity: O(log n)
     * Space Complexity: O(1)
     * 
     * @param sortedList The pre-sorted list of elements to search through
     * @param target The element to find in the list
     * @return Index of the target element, or -1 if not found
     * @throws IllegalArgumentException If input list is null or unsorted
     */
    public static <T extends Comparable<T>> int binarySearch(List<T> sortedList, T target) {
        // Validate input parameters
        if (sortedList == null || sortedList.isEmpty()) {
            throw new IllegalArgumentException("Search list cannot be null or empty");
        }

        // Verify list is sorted to ensure binary search works correctly
        if (!isSorted(sortedList)) {
            throw new IllegalArgumentException("Input list must be sorted in ascending order");
        }

        int left = 0;
        int right = sortedList.size() - 1;

        // Core binary search algorithm
        while (left <= right) {
            // Prevent potential integer overflow using safer mid-point calculation
            int mid = left + (right - left) / 2;

            // Compare current element with target
            int comparison = sortedList.get(mid).compareTo(target);

            if (comparison == 0) {
                // Target found, return its index
                return mid;
            } else if (comparison < 0) {
                // Target is in the right half of the list
                left = mid + 1;
            } else {
                // Target is in the left half of the list
                right = mid - 1;
            }
        }

        // Target not found in the list
        return -1;
    }

    /**
     * Checks if a list is sorted in ascending order.
     * 
     * @param list The list to check for sorted order
     * @return Boolean indicating whether the list is sorted
     */
    private static <T extends Comparable<T>> boolean isSorted(List<T> list) {
        for (int i = 1; i < list.size(); i++) {
            if (list.get(i - 1).compareTo(list.get(i)) > 0) {
                return false;
            }
        }
        return true;
    }

    /**
     * Example usage and demonstration of search algorithm.
     * 
     * @param args Command line arguments (not used)
     */
    public static void main(String[] args) {
        // Sample sorted list of products
        List<String> productCatalog = Arrays.asList(
            "Apple", "Banana", "Cherry", "Date", "Elderberry", 
            "Fig", "Grape", "Honeydew", "Imbe", "Jackfruit"
        );

        // Demonstrate successful search
        String searchItem = "Cherry";
        int result = binarySearch(productCatalog, searchItem);
        System.out.println("Search for '" + searchItem + "': " 
            + (result != -1 ? "Found at index " + result : "Not found"));

        // Demonstrate unsuccessful search
        searchItem = "Kiwi";
        result = binarySearch(productCatalog, searchItem);
        System.out.println("Search for '" + searchItem + "': " 
            + (result != -1 ? "Found at index " + result : "Not found"));
    }
}

