// Function to generate Fibonacci numbers up to a certain count
std::vector<int> generateFibonacci(int count) {
    std::vector<int> fib;
    if (count >= 1) fib.push_back(0);
    if (count >= 2) fib.push_back(1);
    for (int i = 2; i < count; ++i) {
        fib.push_back(fib[i - 1] + fib[i - 2]);
    }
    return fib;
}

// Function to draw the Fibonacci spiral based on the Fibonacci sequence
void drawFibonacciSpiral(sf::RenderWindow &window, const std::vector<int> &fib) {
    float angle = 0; // Starting angle in radians
    sf::Vector2f position(window.getSize().x / 2, window.getSize().y / 2); // Center position

    // Circle color settings
    sf::Color color(100, 250, 150); // Light green color

    for (size_t i = 0; i < fib.size(); ++i) {
        // Create a rectangle shape representing the Fibonacci squares
        sf::RectangleShape square(sf::Vector2f(fib[i], fib[i]));
        square.setFillColor(color);
        square.setOrigin(fib[i] / 2, fib[i] / 2); // Set origin to center for rotation
        square.setPosition(position);

        // Rotate the square accordingly
        square.setRotation(angle * 180 / 3.14159); // Convert radians to degrees

        // Draw the square on the window
        window.draw(square);

        // Update the position for the next square
        position.x += fib[i] * cos(angle);
        position.y += fib[i] * sin(angle);

        // Update the angle for the next square
        angle += 3.14159 / 2; // Rotate by 90 degrees (pi/2)
    }
}

// Main function to set up the window and render the pattern
int main() {
    const int fibCount = 10; // Number of Fibonacci numbers to generate
    std::vector<int> fib = generateFibonacci(fibCount); // Generate the Fibonacci sequence

    // Create the render window
    sf::RenderWindow window(sf::VideoMode(800, 600), "Fibonacci Spiral Pattern");

    while (window.isOpen()) {
        sf::Event event;
        while (window.pollEvent(event)) {
            if (event.type == sf::Event::Closed)
                window.close(); // Close the window on close event
        }

        window.clear(sf::Color::Black); // Clear the window with black color
        drawFibonacciSpiral(window, fib); // Draw the Fibonacci spiral
        window.display(); // Display the contents of the window
    }

    return 0; // Exit the application
}
