#include <iostream>

// Definition for singly-linked list.
struct ListNode {
    int val;
    ListNode *next;
    ListNode(int x) : val(x), next(nullptr) {}
};

// Function to unify two sorted linked lists into a single sorted linked list
ListNode* mergeTwoLists(ListNode* l1, ListNode* l2) {
    // Create a dummy node to serve as the starting point of the merged list
    ListNode dummy(0);
    ListNode* tail = &dummy;

    // Traverse both lists while both have elements
    while (l1 != nullptr && l2 != nullptr) {
        // Compare the values of the nodes in both lists
        if (l1->val < l2->val) {
            // Attach the smaller node to the merged list
            tail->next = l1;
            // Move to the next node in list 1
            l1 = l1->next;
        } else {
            // Attach the smaller node to the merged list
            tail->next = l2;
            // Move to the next node in list 2
            l2 = l2->next;
        }
        // Move the tail pointer forward
        tail = tail->next;
    }

    // If there are remaining nodes in either list, attach them
    if (l1 != nullptr) {
        tail->next = l1; // Attach the remainder of list 1
    } else {
        tail->next = l2; // Attach the remainder of list 2
    }

    // The merged list starts from the next of the dummy node
    return dummy.next;
}

