```cpp
#include <iostream>

// Node structure for the singly linked list
struct ListNode {
    int val;
    ListNode* next;
    ListNode(int x) : val(x), next(nullptr) {}
};

/**
 * Merges two sorted linked lists into one sorted linked list.
 * 
 * This function takes two sorted linked lists and merges them in ascending order
 * using an iterative approach without recursion for better space efficiency.
 * 
 * @param l1 Head pointer of the first sorted linked list
 * @param l2 Head pointer of the second sorted linked list
 * @return Head pointer of the merged sorted linked list
 * 
 * Time Complexity: O(n + m), where n and m are lengths of the two lists
 * Space Complexity: O(1) (excluding the output list)
 */
ListNode* mergeTwoLists(ListNode* l1, ListNode* l2) {
    // Create a dummy node to serve as the starting point of the merged list
    ListNode dummy(0);
    // 'tail' pointer will help us build the merged list
    ListNode* tail = &dummy;

    // Traverse both lists until we reach the end of one
    while (l1 != nullptr && l2 != nullptr) {
        if (l1->val <= l2->val) {
            // If l1's value is smaller, append it to the merged list
            tail->next = l1;
            l1 = l1->next;
        } else {
            // Otherwise append l2's node
            tail->next = l2;
            l2 = l2->next;
        }
        // Move the tail pointer forward
        tail = tail->next;
    }

    // Append the remaining elements of l1 or l2
    tail->next = (l1 != nullptr) ? l1 : l2;

    // The merged list starts after the dummy node
    return dummy.next;
}

/**
 * Utility function to print a linked list
 * 
 * @param head Head pointer of the linked list to print
 */
void printList(ListNode* head) {
    while (head != nullptr) {
        std::cout << head->val << " ";
        head = head->next;
    }
    std::cout << std::endl;
}

/**
 * Utility function to create a linked list from an array
 * 
 * @param values Array of integers to convert to a linked list
 * @param n Length of the array
 * @return Head pointer of the created linked list
 */
ListNode* createList(int values[], int n) {
    if (n == 0) return nullptr;
    ListNode* head = new ListNode(values[0]);
    ListNode* current = head;
    for (int i = 1; i < n; ++i) {
        current->next = new ListNode(values[i]);
        current = current->next;
    }
    return head;
}

