class Solution {
    /**
     * Computes the n-th TriFib number efficiently using dynamic programming.
     * The TriFib sequence is defined as:
     * trifib(0) = 0
     * trifib(1) = 0
     * trifib(2) = 1
     * trifib(n) = trifib(n-1) + trifib(n-2) + 2 * trifib(n-3) for n > 2
     *
     * @param n The index of the TriFib number to compute (must be non-negative)
     * @return The n-th TriFib number
     * @throws IllegalArgumentException if n is negative
     */
    public int trifib(int n) {
        // Handle edge cases and invalid input
        if (n < 0) {
            throw new IllegalArgumentException("Input must be non-negative");
        }

        // Base cases
        if (n == 0 || n == 1) {
            return 0;
        }
        if (n == 2) {
            return 1;
        }

        // Initialize DP array to store computed values
        int[] dp = new int[n + 1];
        dp[0] = 0;
        dp[1] = 0;
        dp[2] = 1;

        // Compute values from 3 to n using the recurrence relation
        for (int i = 3; i <= n; i++) {
            dp[i] = dp[i - 1] + dp[i - 2] + 2 * dp[i - 3];
        }

        return dp[n];
    }

