bool is_not_alpha(char c) {
    return !std::isalpha(c);
}

// Function to remove all non-alphabetic characters from a string
std::string remove_special_characters(std::string s) {
    // Use the erase-remove idiom to remove non-alphabetic characters
    s.erase(std::remove_if(s.begin(), s.end(), is_not_alpha), s.end());
    return s;
}

int main() {
    std::string str = "Hello!&@ This#%^$ is a string";
    std::string result = remove_special_characters(str);
    std::cout << result << std::endl;  // Output: "HelloThisisastring"
    return 0;
}
```

