```java
import java.util.concurrent.locks.ReentrantLock;

class CircularLinkedList {
    // Node class representing each element in the circular linked list
    private class Node {
        String id;           // Unique identifier
        int value;          // Corresponding numerical value
        Node next;          // Pointer to the next node

        Node(String id, int value) {
            this.id = id;
            this.value = value;
            this.next = null;
        }
    }

    private Node head;         // Head of the circular linked list
    private final ReentrantLock lock = new ReentrantLock(); // Lock for thread-safety

    // Method to insert a new node into the circular linked list
    public void insert(String id, int value) {
        lock.lock(); // Acquire the lock to ensure thread safety
        try {
            Node newNode = new Node(id, value);
            if (head == null) { // If the list is empty
                head = newNode;
                head.next = head; // Point to itself to make it circular
            } else {
                Node current = head;
                while (current.next != head) { // Traverse to the last node
                    current = current.next;
                }
                current.next = newNode; // Link the new node
                newNode.next = head; // Complete the circular link
            }
        } finally {
            lock.unlock(); // Release the lock
        }
    }

    // Method to delete a node by its unique identifier
    public boolean delete(String id) {
        lock.lock(); // Acquire the lock for thread safety
        try {
            if (head == null) return false; // List is empty

            Node current = head;
            Node previous = null;

            // Traverse the list to find the node to delete
            do {
                if (current.id.equals(id)) {
                    if (previous == null) { // Node to delete is the head
                        if (current.next == head) { // Only one node in the list
                            head = null;
                        } else {
                            // Find the last node to re-link it to the new head
                            Node last = head;
                            while (last.next != head) {
                                last = last.next;
                            }
                            last.next = current.next; // bypass the current node
                            head = current.next; // Update head
                        }
                    } else {
                        previous.next = current.next; // Bypass the current node
                    }
                    return true; // Node was deleted
                }
                previous = current;
                current = current.next;
            } while (current != head);

            return false; // Node not found
        } finally {
            lock.unlock(); // Release the lock
        }
    }

    // Method to search for a node by its unique identifier
    public Integer search(String id) {
        lock.lock(); // Acquire the lock for thread safety
        try {
            if (head == null) return null; // List is empty

            Node current = head;
            do {
                if (current.id.equals(id)) {
                    return current.value; // Found the node, return its value
                }
                current = current.next;
            } while (current != head);

            return null; // Node not found
        } finally {
            lock.unlock(); // Release the lock
        }
    }

    // Method to display the contents of the circular linked list
    public void display() {
        lock.lock(); // Acquire the lock for thread safety
        try {
            if (head == null) {
                System.out.println("List is empty.");
                return;
            }
            Node current = head;
            do {
                System.out.println("ID: " + current.id + ", Value: " + current.value);
                current = current.next;
            } while (current != head);
        } finally {
            lock.unlock(); // Release the lock
        }
    }
}

// Example usage of CircularLinkedList
public class Main {
    public static void main(String[] args) {
        CircularLinkedList list = new CircularLinkedList();

        // Inserting nodes
        list.insert("A", 10);
        list.insert("B", 20);
        list.insert("C", 30);

        // Display the list
        list.display();

        // Search for node "B"
        System.out.println("Value of B: " + list.search("B"));

        // Delete node "A"
        list.delete("A");
        System.out.println("After deleting A:");
        list.display();
    }
}
```

