import java.util.Arrays;

/**
 * Checks if two strings are anagrams of each other.
 * An anagram is a word or phrase formed by rearranging the letters of a different word or phrase,
 * typically using all the original letters exactly once.
 * 
 * @param str1 The first string to compare
 * @param str2 The second string to compare
 * @return true if the strings are anagrams, false otherwise
 * 
 * Edge cases handled:
 * - Null strings (returns false)
 * - Different length strings (returns false)
 * - Case sensitivity (converts to lowercase before comparison)
 * - Whitespace (ignores whitespace by removing it)
 */
public static boolean areAnagrams(String str1, String str2) {
    // Handle null inputs
    if (str1 == null || str2 == null) {
        return false;
    }

    // Remove all whitespace and convert to lowercase
    String s1 = str1.replaceAll("\\s", "").toLowerCase();
    String s2 = str2.replaceAll("\\s", "").toLowerCase();

    // If lengths are different, they can't be anagrams
    if (s1.length() != s2.length()) {
        return false;
    }

    // Convert strings to char arrays
    char[] charArray1 = s1.toCharArray();
    char[] charArray2 = s2.toCharArray();

    // Sort the char arrays
    Arrays.sort(charArray1);
    Arrays.sort(charArray2);

    // Compare the sorted arrays
    return Arrays.equals(charArray1, charArray2);
}

// Example usage:
// System.out.println(areAnagrams("listen", "silent"));  // true
// System.out.println(areAnagrams("Hello", "World"));    // false
// System.out.println(areAnagrams("Dormitory", "Dirty room"));  // true
