import json

def process_people_data(json_string):
    """
    Processes a JSON string containing people's data to extract education and occupation details,
    calculates average age, and restructures the information into a new JSON format.

    Args:
        json_string (str/dict): Input JSON string or dictionary containing people's data.

    Returns:
        dict: Restructured JSON containing extracted details and average age.
    """
    # Load JSON if input is string, otherwise use directly
    data = json.loads(json_string) if isinstance(json_string, str) else json_string

    total_age = 0
    people_details = []

    for person in data['people']:
        # Extract basic info
        name = person['name']
        age = person['age']
        total_age += age

        # Extract nested education details
        education = person['details']['education']
        school = education['school']
        major = education['major']

        # Extract nested occupation details
        occupation = person['details']['occupation']
        current_job = occupation['current']
        experience = occupation['experience']

        # Create restructured person entry
        person_detail = {
            'name': name,
            'education': {
                'school': school,
                'major': major
            },
            'occupation': {
                'title': current_job,
                'years_experience': experience
            }
        }
        people_details.append(person_detail)

    # Calculate average age (handle empty list case)
    average_age = total_age / len(data['people']) if data['people'] else 0

    # Create final output structure
    result = {
        'people': people_details,
        'statistics': {
            'average_age': round(average_age, 2)
        }
    }

