        this.dataSet = Objects.requireNonNull(dataSet, "Dataset cannot be null");
    }

    /**
     * Performs a case-insensitive partial match search across multiple fields.
     * 
     * @param searchTerm The term to search for
     * @param searchFields Functional interfaces to extract searchable fields from each item
     * @return List of matching items
     * @throws IllegalArgumentException if search term is null or empty
     */
    public List<T> search(String searchTerm, SearchField<T>... searchFields) {
        validateSearchTerm(searchTerm);

        String normalizedTerm = searchTerm.trim().toLowerCase();

        return dataSet.stream()
            .filter(item -> matchesAnyField(item, normalizedTerm, searchFields))
            .collect(Collectors.toList());
    }

    /**
     * Performs a more advanced search with fuzzy matching and ranking.
     * 
     * @param searchTerm The term to search for
     * @param maxResults Maximum number of results to return
     * @param searchFields Functional interfaces to extract searchable fields
     * @return Ranked list of top matching items
     */
    public List<T> advancedSearch(String searchTerm, int maxResults, SearchField<T>... searchFields) {
        validateSearchTerm(searchTerm);

        String normalizedTerm = searchTerm.trim().toLowerCase();

        return dataSet.stream()
            .map(item -> new ScoredItem<>(item, calculateRelevanceScore(item, normalizedTerm, searchFields)))
            .filter(scoredItem -> scoredItem.score > 0)
            .sorted(Comparator.comparingDouble(ScoredItem::getScore).reversed())
            .limit(maxResults)
            .map(ScoredItem::getItem)
            .collect(Collectors.toList());
    }

    /**
     * Functional interface for extracting searchable fields from items.
     * @param <T> Type of item being searched
     */
    @FunctionalInterface
    public interface SearchField<T> {
        String extractField(T item);
    }

    /**
     * Validates the search term, ensuring it's not null or empty.
     * 
     * @param searchTerm Search term to validate
     * @throws IllegalArgumentException if search term is invalid
     */
    private void validateSearchTerm(String searchTerm) {
        if (searchTerm == null || searchTerm.trim().isEmpty()) {
            throw new IllegalArgumentException("Search term cannot be null or empty");
        }
    }

    /**
     * Checks if an item matches the search term across multiple fields.
     * 
     * @param item Item to check
     * @param normalizedTerm Normalized search term
     * @param searchFields Fields to search within
     * @return Boolean indicating if the item matches
     */
    private boolean matchesAnyField(T item, String normalizedTerm, SearchField<T>[] searchFields) {
        return Arrays.stream(searchFields)
            .anyMatch(field -> {
                String extractedField = field.extractField(item);
                return extractedField != null && 
                       extractedField.toLowerCase().contains(normalizedTerm);
            });
    }

    /**
     * Calculates a relevance score for advanced search.
     * 
     * @param item Item to score
     * @param normalizedTerm Normalized search term
     * @param searchFields Fields to calculate score from
     * @return Relevance score
     */
    private double calculateRelevanceScore(T item, String normalizedTerm, SearchField<T>[] searchFields) {
        return Arrays.stream(searchFields)
            .mapToDouble(field -> {
                String extractedField = field.extractField(item);
                if (extractedField == null) return 0.0;

                String normalizedField = extractedField.toLowerCase();
                if (normalizedField.equals(normalizedTerm)) return 1.0;
                if (normalizedField.startsWith(normalizedTerm)) return 0.8;
                if (normalizedField.contains(normalizedTerm)) return 0.5;
                return 0.0;
            })
            .max()
            .orElse(0.0);
    }

    /**
     * Internal class to track scored items during advanced search.
     * @param <T> Type of item being scored
     */
    private static class ScoredItem<T> {
        private final T item;
        private final double score;

        ScoredItem(T item, double score) {
            this.item = item;
            this.score = score;
        }

        T getItem() { return item; }
        double getScore() { return score; }
    }
}

```


