def is_prime(num):
    """Check if a number is prime."""
    if num <= 1:
        return False
    if num <= 3:
        return True
    if num % 2 == 0 or num % 3 == 0:
        return False
    i = 5
    while i * i <= num:
        if num % i == 0 or num % (i + 2) == 0:
            return False
        i += 6
    return True

def product_of_primes(limit):
    """Calculate the product of all prime numbers under the given limit."""
    product = 1
    for num in range(2, limit):
        if is_prime(num):
            product *= num
    return product

