def merge_dicts(dict1, dict2):
    for k, v in dict2.items():
        # If the key exists in both dictionaries
        if k in dict1:
            # Check if both values are dictionaries
            if isinstance(dict1[k], dict) and isinstance(v, dict):
                # Recursively merge the dictionaries
                dict1[k] = merge_dicts(dict1[k], v)
            else:
                # Conflict: take the value from dict2
                dict1[k] = v
        else:
            # Key does not exist in dict1, add it
            dict1[k] = v
    return dict1

# Example usage
