```cpp
#include <iostream>
#include <vector>

// Function to sort an array in ascending order using Bubble Sort
void sortArrayAscending(std::vector<int>& arr) {
    int n = arr.size();
    bool swapped;

    // Outer loop to iterate through the array
    for (int i = 0; i < n - 1; ++i) {
        swapped = false;

        // Inner loop to compare adjacent elements
        for (int j = 0; j < n - i - 1; ++j) {
            if (arr[j] > arr[j + 1]) {
                // Swap elements if they are in the wrong order
                std::swap(arr[j], arr[j + 1]);
                swapped = true;
            }
        }

        // If no elements were swapped, the array is already sorted
        if (!swapped) {
            break;
        }
    }
}

int main() {
    // Initialize an unsorted array
    std::vector<int> unsortedArr = {45, 21, 12, 56, 7, 47, 34};

    // Print the unsorted array
    std::cout << "Unsorted Array: ";
    for (int num : unsortedArr) {
        std::cout << num << " ";
    }
    std::cout << std::endl;

    // Call the sorting function
    sortArrayAscending(unsortedArr);

    // Print the sorted array
    std::cout << "Sorted Array (Ascending): ";
    for (int num : unsortedArr) {
        std::cout << num << " ";
    }
    std::cout << std::endl;

    return 0;
}
```

