    if (n <= 1) return n;
    return fibonacci(n - 1) + fibonacci(n - 2);
}

// Function to draw the Fibonacci spiral
void drawFibonacciSpiral(sf::RenderWindow& window, int terms) {
    float angle = 0.0f;
    float x = 400.0f; // Starting position (center of window)
    float y = 300.0f;

    for (int i = 0; i < terms; ++i) {
        unsigned long long fib = fibonacci(i);
        sf::VertexArray line(sf::PrimitiveType::LinesStrip, 100); // For drawing spiral

        for (int j = 0; j <= 100; ++j) {
            float t = j / 100.0f * (M_PI / 2); // Quarter circle
            float radius = static_cast<float>(fib);
            line[j].position = sf::Vector2f(x + radius * cos(angle + t), y + radius * sin(angle + t));
            line[j].color = sf::Color::Green; // Set color to green
        }

        window.draw(line);

        // Update position and angle for the next quarter circle
        x += fib * cos(angle);
        y += fib * sin(angle);
        angle += M_PI / 2; // Rotate by 90 degrees
    }
}

