public class PrimeNumberPrinter {
    /**
     * Prints all prime numbers within a specified range using the Sieve of Eratosthenes algorithm.
     * 
     * @param min The minimum value of the range (inclusive)
     * @param max The maximum value of the range (inclusive)
     */
    public static void printPrimeNumbers(int min, int max) {
        // Create a boolean array to mark prime and non-prime numbers
        boolean[] isPrime = new boolean[max + 1];

        // Initialize all numbers as prime
        for (int i = 2; i <= max; i++) {
            isPrime[i] = true;
        }

        // Apply Sieve of Eratosthenes
        for (int p = 2; p * p <= max; p++) {
            if (isPrime[p]) {
                // Mark multiples of p as non-prime
                for (int i = p * p; i <= max; i += p) {
                    isPrime[i] = false;
                }
            }
        }

        // Print prime numbers within the given range
        System.out.println("Prime numbers between " + min + " and " + max + ":");
        for (int num = Math.max(2, min); num <= max; num++) {
            if (isPrime[num]) {
                System.out.print(num + " ");
            }
        }
    }

    /**
     * Main method to demonstrate prime number printing.
     * 
     * @param args Command-line arguments (not used)
     */
    public static void main(String[] args) {
        printPrimeNumbers(2, 30);
    }
}

