// Node structure for doubly linked list
struct Node {
    int data;
    Node* prev;
    Node* next;
};

// DoublyLinkedList class
class DoublyLinkedList {
private:
    Node* head; // Empty head node
    int length;

public:
    // Constructor
    DoublyLinkedList() {
        head = new Node();
        head->prev = nullptr;
        head->next = nullptr;
        head->data = 0; // Not used, as head is empty
        length = 0;
    }

    // Destructor to free memory
    ~DoublyLinkedList() {
        Node* current = head;
        while (current != nullptr) {
            Node* next = current->next;
            delete current;
            current = next;
        }
    }

    // Add a node at the end of the list
    void append(int value) {
        Node* newNode = new Node();
        newNode->data = value;
        newNode->next = nullptr;

        Node* current = head;
        while (current->next != nullptr) {
            current = current->next;
        }

        current->next = newNode;
        newNode->prev = current;
        length++;
    }

    // Add a node at a specific position (0-based indexing)
    void insert(int position, int value) {
        if (position < 0 || position > length) {
            cout << "Invalid position!" << endl;
            return;
        }

        Node* newNode = new Node();
        newNode->data = value;

        Node* current = head;
        for (int i = 0; i < position; i++) {
            current = current->next;
        }

        newNode->next = current->next;
        if (current->next != nullptr) {
            current->next->prev = newNode;
        }
        current->next = newNode;
        newNode->prev = current;
        length++;
    }

    // Delete a node at a specific position (0-based indexing)
    void remove(int position) {
        if (position < 0 || position >= length) {
            cout << "Invalid position!" << endl;
            return;
        }

        Node* current = head->next; // Skip the empty head
        for (int i = 0; i < position; i++) {
            current = current->next;
        }

        if (current->prev != nullptr) {
            current->prev->next = current->next;
        }
        if (current->next != nullptr) {
            current->next->prev = current->prev;
        }

        delete current;
        length--;
    }

    // Display the list from head to tail
    void displayForward() {
        Node* current = head->next; // Skip the empty head
        cout << "List (forward): ";
        while (current != nullptr) {
            cout << current->data << " ";
            current = current->next;
        }
        cout << endl;
    }

    // Display the list from tail to head
    void displayBackward() {
        if (length == 0) {
            cout << "List is empty!" << endl;
            return;
        }

        // Find the tail node
        Node* current = head;
        while (current->next != nullptr) {
            current = current->next;
        }

        cout << "List (backward): ";
        while (current != head) {
            cout << current->data << " ";
            current = current->prev;
        }
        cout << endl;
    }

    // Get the current length of the list
    int getLength() {
        return length;
    }
};

