    private final ReentrantLock listLock = new ReentrantLock();
    private final int capacity;
    private int size;

    public CircularLinkedList(int capacity) {
        if (capacity <= 0) {
            throw new IllegalArgumentException("Capacity must be positive");
        }
        this.capacity = capacity;
        this.size = 0;
        this.head = null;
    }

    public boolean insert(String id, int value) {
        if (id == null) {
            throw new IllegalArgumentException("ID cannot be null");
        }

        listLock.lock();
        try {
            if (size >= capacity) {
                return false;
            }

            // Check for duplicate ID
            if (head != null) {
                Node current = head;
                do {
                    if (current.id.equals(id)) {
                        return false;
                    }
                    current = current.next;
                } while (current != head);
            }

            Node newNode = new Node(id, value);
            if (head == null) {
                head = newNode;
                head.next = head; // circular reference
            } else {
                Node tail = head;
                while (tail.next != head) {
                    tail = tail.next;
                }
                tail.next = newNode;
                newNode.next = head;
            }
            size++;
            return true;
        } finally {
            listLock.unlock();
        }
    }

    public boolean delete(String id) {
        if (id == null) {
            throw new IllegalArgumentException("ID cannot be null");
        }

        listLock.lock();
        try {
            if (head == null) {
                return false;
            }

            Node prev = null;
            Node current = head;
            boolean found = false;

            do {
                if (current.id.equals(id)) {
                    found = true;
                    break;
                }
                prev = current;
                current = current.next;
            } while (current != head);

            if (!found) {
                return false;
            }

            if (current == head) {
                if (head.next == head) { // only one node
                    head = null;
                } else {
                    Node tail = head;
                    while (tail.next != head) {
                        tail = tail.next;
                    }
                    tail.next = head.next;
                    head = head.next;
                }
            } else {
                prev.next = current.next;
            }
            size--;
            return true;
        } finally {
            listLock.unlock();
        }
