    public ThreadSafeCircularLinkedList(int capacity) {
        if (capacity <= 0) {
            throw new IllegalArgumentException("Capacity must be positive");
        }
        this.capacity = capacity;
        this.head = null;
        this.size = 0;
    }

    // Method to insert a new node
    public void insert(K identifier, V value) {
        lock.lock();
        try {
            // Check if list is at capacity
            if (size >= capacity) {
                throw new IllegalStateException("List is at maximum capacity");
            }

            // Check for duplicate identifier
            if (findNodeByIdentifier(identifier) != null) {
                throw new IllegalArgumentException("Identifier already exists");
            }

            Node newNode = new Node(identifier, value);

            // If list is empty
            if (head == null) {
                head = newNode;
                head.next = head;
                head.prev = head;
            } else {
                // Insert at the end of the list
                Node tail = head.prev;
                tail.next = newNode;
                newNode.prev = tail;
                newNode.next = head;
                head.prev = newNode;
            }
            size++;
        } finally {
            lock.unlock();
        }
    }

