            }

            // If only one node exists
            if (size == 1) {
                head = null;
            } else {
                nodeToDelete.prev.next = nodeToDelete.next;
                nodeToDelete.next.prev = nodeToDelete.prev;

                // Update head if deleted node is the head
                if (nodeToDelete == head) {
                    head = nodeToDelete.next;
                }
            }
            size--;
        } finally {
            lock.unlock();
        }
    }

    // Method to search for a node by identifier
    public V search(K identifier) {
        lock.lock();
        try {
            Node foundNode = findNodeByIdentifier(identifier);
            return foundNode != null ? foundNode.value : null;
        } finally {
            lock.unlock();
        }
