        self.pride = pride

    def make_sound(self):
        return "Roar"

    def hunt(self):
        return f"The lion hunts with its pride of {self.pride}."


class Elephant(Animal):
    def __init__(self, herd):
        super().__init__("Elephant", "Grasslands", "Herbivore", 60)
        self.herd = herd

    def make_sound(self):
