        return f"The elephant trumpets to communicate with its herd of {self.herd}."


class Eagle(Animal):
    def __init__(self, nest):
        super().__init__("Eagle", "Mountains", "Carnivore", 20)
        self.nest = nest

    def make_sound(self):
        return "Screech"

    def soar(self):
        return f"The eagle soars high above its nest at {self.nest}."


class Habitat:
    def __init__(self):
        self.animals = []

    def add_animal(self, animal):
        if not isinstance(animal, Animal):
            raise TypeError("Only Animal instances can be added.")
        self.animals.append(animal)

    def remove_animal(self, species):
        found = False
        for animal in self.animals:
            if animal.species == species:
                self.animals.remove(animal)
                found = True
                break
        if not found:
            raise ValueError(f"No animal of species {species} found in habitat.")

    def count_animals(self):
        return len(self.animals)

    def average_lifespan(self):
        if not self.animals:
            return 0
        total_lifespan = sum(animal.lifespan for animal in self.animals)
        return total_lifespan / len(self.animals)


