        Map<Integer, Integer> occurrenceMap = new HashMap<>();

        for (Object obj : arr) {
            if (obj instanceof Integer) {
                int num = (Integer) obj;
                occurrenceMap.put(num, occurrenceMap.getOrDefault(num, 0) + 1);
            } else {
                throw new IllegalArgumentException("Array contains non-integer value: " + obj);
