import java.sql.*;
import java.util.Scanner;

/**
 * EmployeeDatabaseManager - A program to manage employee records in a database.
 * Supports adding, removing, and updating employee information.
 */
public class EmployeeDatabaseManager {
    // Database connection details
    private static final String DB_URL = "jdbc:mysql://localhost:3306/employee_db";
    private static final String DB_USER = "root";
    private static final String DB_PASSWORD = "password";

    private Connection connection;
    private Scanner scanner;

    /**
     * Main method to start the employee database manager.
     */
    public static void main(String[] args) {
        EmployeeDatabaseManager manager = new EmployeeDatabaseManager();
        manager.start();
    }

    /**
     * Initialize the database manager and establish connection.
     */
    public EmployeeDatabaseManager() {
        scanner = new Scanner(System.in);
        try {
            connection = DriverManager.getConnection(DB_URL, DB_USER, DB_PASSWORD);
            createEmployeeTableIfNotExists();
        } catch (SQLException e) {
            System.err.println("Database connection error: " + e.getMessage());
            System.exit(1);
        }
    }

    /**
     * Create the employees table if it doesn't exist.
     */
    private void createEmployeeTableIfNotExists() throws SQLException {
        String createTableSQL = "CREATE TABLE IF NOT EXISTS employees (" +
                "id INT AUTO_INCREMENT PRIMARY KEY, " +
                "name VARCHAR(100) NOT NULL, " +
                "position VARCHAR(100), " +
                "salary DECIMAL(10, 2), " +
                "hire_date DATE)";
        try (Statement statement = connection.createStatement()) {
            statement.execute(createTableSQL);
        }
    }

    /**
     * Start the interactive menu for employee management.
     */
    public void start() {
        while (true) {
            printMenu();
            int choice = scanner.nextInt();
            scanner.nextLine(); // Consume newline

            try {
                switch (choice) {
                    case 1:
                        addEmployee();
                        break;
                    case 2:
                        updateEmployee();
                        break;
                    case 3:
                        removeEmployee();
                        break;
                    case 4:
                        viewAllEmployees();
                        break;
                    case 5:
                        System.out.println("Exiting program...");
                        connection.close();
                        scanner.close();
                        return;
                    default:
                        System.out.println("Invalid choice. Please try again.");
                }
            } catch (SQLException e) {
                System.err.println("Database error: " + e.getMessage());
            }
        }
    }

    /**
     * Print the main menu options.
     */
    private void printMenu() {
        System.out.println("\nEmployee Database Manager");
        System.out.println("1. Add Employee");
        System.out.println("2. Update Employee");
        System.out.println("3. Remove Employee");
        System.out.println("4. View All Employees");
        System.out.println("5. Exit");
        System.out.print("Enter your choice: ");
    }

    /**
     * Add a new employee to the database.
     */
    private void addEmployee() throws SQLException {
        System.out.println("\nAdd New Employee");
        System.out.print("Enter employee name: ");
        String name = scanner.nextLine();

        System.out.print("Enter position: ");
        String position = scanner.nextLine();

        System.out.print("Enter salary: ");
        double salary = scanner.nextDouble();

        System.out.print("Enter hire date (YYYY-MM-DD): ");
        String hireDate = scanner.next();

        String insertSQL = "INSERT INTO employees (name, position, salary, hire_date) VALUES (?, ?, ?, ?)";
        try (PreparedStatement statement = connection.prepareStatement(insertSQL)) {
            statement.setString(1, name);
            statement.setString(2, position);
            statement.setDouble(3, salary);
            statement.setDate(4, Date.valueOf(hireDate));

            int rowsAffected = statement.executeUpdate();
            System.out.println(rowsAffected + " employee(s) added successfully.");
        }
    }

    /**
     * Update an existing employee's information.
     */
    private void updateEmployee() throws SQLException {
        System.out.println("\nUpdate Employee");
        System.out.print("Enter employee ID to update: ");
        int id = scanner.nextInt();
        scanner.nextLine(); // Consume newline

        System.out.print("Enter new name (leave blank to keep current): ");
        String name = scanner.nextLine();

        System.out.print("Enter new position (leave blank to keep current): ");
        String position = scanner.nextLine();

        System.out.print("Enter new salary (enter 0 to keep current): ");
        double salary = scanner.nextDouble();

        System.out.print("Enter new hire date (YYYY-MM-DD, leave blank to keep current): ");
        String hireDateStr = scanner.next();
        Date hireDate = hireDateStr.isEmpty() ? null : Date.valueOf(hireDateStr);

        StringBuilder updateSQL = new StringBuilder("UPDATE employees SET ");
        boolean needsComma = false;

        if (!name.isEmpty()) {
            updateSQL.append("name = ?");
            needsComma = true;
        }
        if (!position.isEmpty()) {
            if (needsComma) updateSQL.append(", ");
            updateSQL.append("position = ?");
            needsComma = true;
        }
        if (salary > 0) {
            if (needsComma) updateSQL.append(", ");
            updateSQL.append("salary = ?");
            needsComma = true;
        }
        if (hireDate != null) {
            if (needsComma) updateSQL.append(", ");
            updateSQL.append("hire_date = ?");
        }

        updateSQL.append(" WHERE id = ?");

        try (PreparedStatement statement = connection.prepareStatement(updateSQL.toString())) {
            int paramIndex = 1;

            if (!name.isEmpty()) {
                statement.setString(paramIndex++, name);
            }
            if (!position.isEmpty()) {
                statement.setString(paramIndex++, position);
            }
            if (salary > 0) {
                statement.setDouble(paramIndex++, salary);
            }
            if (hireDate != null) {
                statement.setDate(paramIndex++, hireDate);
            }

            statement.setInt(paramIndex, id);

            int rowsAffected = statement.executeUpdate();
            if (rowsAffected > 0) {
                System.out.println("Employee updated successfully.");
            } else {
                System.out.println("No employee found with ID: " + id);
            }
        }
    }

    /**
     * Remove an employee from the database.
     */
    private void removeEmployee() throws SQLException {
        System.out.println("\nRemove Employee");
        System.out.print("Enter employee ID to remove: ");
        int id = scanner.nextInt();

        String deleteSQL = "DELETE FROM employees WHERE id = ?";
        try (PreparedStatement statement = connection.prepareStatement(deleteSQL)) {
            statement.setInt(1, id);

            int rowsAffected = statement.executeUpdate();
            if (rowsAffected > 0) {
                System.out.println("Employee removed successfully.");
            } else {
                System.out.println("No employee found with ID: " + id);
            }
        }
    }

    /**
     * View all employees in the database.
     */
    private void viewAllEmployees() throws SQLException {
        System.out.println("\nEmployee List");
        String selectSQL = "SELECT * FROM employees ORDER BY id";

        try (Statement statement = connection.createStatement();
             ResultSet resultSet = statement.executeQuery(selectSQL)) {

            System.out.printf("%-5s %-20s %-20s %-10s %-12s%n", 
                    "ID", "Name", "Position", "Salary", "Hire Date");
            System.out.println("------------------------------------------------------------");

            while (resultSet.next()) {
                int id = resultSet.getInt("id");
                String name = resultSet.getString("name");
                String position = resultSet.getString("position");
                double salary = resultSet.getDouble("salary");
                Date hireDate = resultSet.getDate("hire_date");

                System.out.printf("%-5d %-20s %-20s %-10.2f %-12s%n", 
                        id, name, position, salary, hireDate);
            }
        }
    }
}
