        std::cerr << "Please enter a positive integer." << std::endl;
        return 1;  // Exit with an error code
    }

    // Calculate and display Fibonacci numbers
    std::vector<int> fibonacciNumbers = calculateFibonacci(n);

    std::cout << "The first " << n << " Fibonacci numbers are: ";
    for (const int& num : fibonacciNumbers) {
        std::cout << num << " ";
