```java 
public class ModularLinkedList {
    // Inner class for Node with encapsulated properties
    private static class Node {
        private int data;
        private Node next;

        // Constructor with clear, focused responsibility
        Node(int data) {
            this.data = data;
            this.next = null;
        }
    }

    // Private head node to encapsulate list implementation
    private Node head;

    // Constructor to initialize an empty list
    public ModularLinkedList() {
        this.head = null;
    }

    /**
     * Add a node at the beginning of the list
     * @param data The value to be added
     */
    public void addAtBeginning(int data) {
        Node newNode = new Node(data);
        newNode.next = head;
        head = newNode;
    }

    /**
     * Add a node at the end of the list
     * @param data The value to be added
     */
    public void addAtEnd(int data) {
        Node newNode = new Node(data);

        // Handle empty list case
        if (head == null) {
            head = newNode;
            return;
        }

        // Traverse to the last node
        Node current = head;
        while (current.next != null) {
            current = current.next;
        }
        current.next = newNode;
    }

    /**
     * Add a node at a specific position
     * @param position The index where the node should be inserted
     * @param data The value to be added
     */
    public void addAtPosition(int position, int data) {
        // Validate position
        if (position < 0) {
            throw new IllegalArgumentException("Position cannot be negative");
        }

        // Special case for inserting at the beginning
        if (position == 0) {
            addAtBeginning(data);
            return;
        }

        Node newNode = new Node(data);
        Node current = head;

        // Traverse to the node before the insertion point
        for (int i = 0; i < position - 1; i++) {
            if (current == null) {
                throw new IndexOutOfBoundsException("Position exceeds list length");
            }
            current = current.next;
        }

        // Insert the new node
        newNode.next = current.next;
        current.next = newNode;
    }

    /**
     * Reverse the linked list in-place
     */
    public void reverse() {
        Node previous = null;
        Node current = head;
        Node next;

        while (current != null) {
            next = current.next;  // Store next node
            current.next = previous;  // Reverse current node's pointer
            previous = current;  // Move previous one step forward
            current = next;  // Move current one step forward
        }
        head = previous;  // Update head to the last node
    }

    /**
     * Print the list for debugging and verification
     */
    public void printList() {
        Node current = head;
        while (current != null) {
            System.out.print(current.data + " -> ");
            current = current.next;
        }
        System.out.println("null");
    }

    // Example usage method
    public static void main(String[] args) {
        ModularLinkedList list = new ModularLinkedList();
        list.addAtEnd(1);
        list.addAtEnd(2);
        list.addAtEnd(3);
        System.out.println("Original List:");
        list.printList();

        list.reverse();
        System.out.println("Reversed List:");
        list.printList();
    }
}

```


