import java.util.ArrayList;
import java.util.Comparator;
import java.util.List;
import java.util.stream.Collectors;

// Data model representing searchable items
class SearchItem {
    private String id;
    private String title;
    private String content;
    private double relevanceScore;

    public SearchItem(String id, String title, String content) {
        this.id = id;
        this.title = title;
        this.content = content;
    }

    // Getters and essential methods
    public String getId() { return id; }
    public String getTitle() { return title; }
    public String getContent() { return content; }
    public double getRelevanceScore() { return relevanceScore; }
    public void setRelevanceScore(double score) { this.relevanceScore = score; }
}

// Search strategy interface for different search algorithms
interface SearchStrategy {
    List<SearchItem> search(List<SearchItem> dataset, String query);
}

// Concrete search implementations
class TitleSearchStrategy implements SearchStrategy {
    @Override
    public List<SearchItem> search(List<SearchItem> dataset, String query) {
        return dataset.stream()
            .filter(item -> item.getTitle().toLowerCase().contains(query.toLowerCase()))
            .collect(Collectors.toList());
    }
}

class ContentSearchStrategy implements SearchStrategy {
    @Override
    public List<SearchItem> search(List<SearchItem> dataset, String query) {
        return dataset.stream()
            .filter(item -> item.getContent().toLowerCase().contains(query.toLowerCase()))
            .collect(Collectors.toList());
    }
}

// Advanced search strategy with relevance scoring
class RelevanceSearchStrategy implements SearchStrategy {
    @Override
    public List<SearchItem> search(List<SearchItem> dataset, String query) {
        return dataset.stream()
            .map(item -> {
                double titleScore = calculateTitleRelevance(item.getTitle(), query);
                double contentScore = calculateContentRelevance(item.getContent(), query);
                double overallScore = (titleScore * 0.6) + (contentScore * 0.4);

                item.setRelevanceScore(overallScore);
                return item;
            })
            .filter(item -> item.getRelevanceScore() > 0)
            .sorted(Comparator.comparing(SearchItem::getRelevanceScore).reversed())
            .collect(Collectors.toList());
    }

    private double calculateTitleRelevance(String title, String query) {
        return title.toLowerCase().contains(query.toLowerCase()) ? 1.0 : 0.0;
    }

    private double calculateContentRelevance(String content, String query) {
        return content.toLowerCase().contains(query.toLowerCase()) ? 0.5 : 0.0;
    }
}

// Search engine coordinator
class SearchEngine {
    private List<SearchItem> dataset;
    private SearchStrategy searchStrategy;

    public SearchEngine(List<SearchItem> dataset) {
        this.dataset = dataset;
        this.searchStrategy = new RelevanceSearchStrategy(); // Default strategy
    }

    public void setSearchStrategy(SearchStrategy strategy) {
        this.searchStrategy = strategy;
    }

    public List<SearchItem> performSearch(String query) {
        return searchStrategy.search(dataset, query);
    }
}

// Demonstration of usage
public class WebSearchApplication {
    public static void main(String[] args) {
        // Sample dataset
        List<SearchItem> dataset = new ArrayList<>();
        dataset.add(new SearchItem("1", "Java Programming", "Learn advanced Java techniques"));
        dataset.add(new SearchItem("2", "Web Development", "Full-stack web development guide"));
        dataset.add(new SearchItem("3", "Search Algorithms", "Efficient search techniques in programming"));

        SearchEngine searchEngine = new SearchEngine(dataset);

        // Perform searches with different strategies
        System.out.println("Title Search Results:");
        searchEngine.setSearchStrategy(new TitleSearchStrategy());
        searchEngine.performSearch("Java").forEach(item -> 
            System.out.println(item.getTitle() + " (ID: " + item.getId() + ")"));

        System.out.println("\nRelevance Search Results:");
        searchEngine.setSearchStrategy(new RelevanceSearchStrategy());
        searchEngine.performSearch("search").forEach(item -> 
            System.out.println(item.getTitle() + " (Relevance: " + item.getRelevanceScore() + ")"));
    }
}

```


