        } catch (IllegalArgumentException e) {
            System.out.println(e.getMessage());
        }
    }

    public static void sumOfPrimesDivisibleByThree(int M, int N, int P) {
        // Validate input
        if (M <= 0 || N <= 0 || P <= 0) {
            throw new IllegalArgumentException("All integers must be positive and non-zero.");
        }

        int maxLimit = Math.max(M, N);
        int sum = 0;

        for (int i = 0; i < maxLimit; i++) {
            if (isPrime(i) && i % 3 == 0 && i < P) {
                sum += i;
            }
        }

        System.out.println("The sum of all prime numbers divisible by 3 and less than P is: " + sum);
    }

    public static boolean isPrime(int num) {
        if (num <= 1) return false; 
        for (int i = 2; i <= Math.sqrt(num); i++) {
            if (num % i == 0) return false;
        }
        return true;
    }
}
```

