public class PrimeSumCalculator {
    /**
     * Validates input parameters and ensures they meet the required constraints.
     * 
     * @param m First input integer
     * @param n Second input integer
     * @param p Upper limit for prime numbers
     * @throws IllegalArgumentException if any input is negative or zero
     */
    private static void validateInputs(int m, int n, int p) {
        if (m <= 0 || n <= 0 || p <= 0) {
            throw new IllegalArgumentException("Inputs must be positive integers greater than zero.");
        }
    }

    /**
     * Determines the maximum of two integers.
     * 
     * @param a First integer
     * @param b Second integer
     * @return The larger of the two integers
     */
    private static int findMaximum(int m, int n) {
        return Math.max(m, n);
    }

    /**
     * Checks if a number is prime.
     * 
     * @param num Number to check for primality
     * @return true if the number is prime, false otherwise
     */
    private static boolean isPrime(int num) {
        if (num <= 1) return false;

        for (int i = 2; i <= Math.sqrt(num); i++) {
            if (num % i == 0) return false;
        }
        return true;
    }

    /**
     * Calculates the sum of prime numbers meeting specific criteria.
     * 
     * @param m First input integer
     * @param n Second input integer
     * @param p Upper limit for prime numbers
     * @return Sum of prime numbers divisible by 3 and less than P
     */
    public static int calculatePrimeSum(int m, int n, int p) {
        // Validate inputs first
        validateInputs(m, n, p);

        // Find the maximum of M and N
        int maxValue = findMaximum(m, n);

        // Collect and sum prime numbers meeting criteria
        int primeSum = 0;
        for (int num = 0; num <= maxValue; num++) {
            if (isPrime(num) && num % 3 == 0 && num < p) {
                primeSum += num;
            }
        }

        return primeSum;
    }

    /**
     * Main method to demonstrate the functionality.
     * 
     * @param args Command line arguments
     */
    public static void main(String[] args) {
        try {
            int result = calculatePrimeSum(20, 25, 15);
            System.out.println("Sum of prime numbers: " + result);
        } catch (IllegalArgumentException e) {
            System.err.println("Error: " + e.getMessage());
        }
    }
}

