```cpp
#include <iostream>
#include <type_traits>

template <typename T>
class Quaternion {
    static_assert(std::is_arithmetic<T>::value, "Quaternion requires a numeric type.");

private:
    T w, x, y, z; // Components of the quaternion (w + xi + yj + zk)

public:
    // Constructor
    Quaternion(T w = T(), T x = T(), T y = T(), T z = T())
        : w(w), x(x), y(y), z(z) {}

    // Quaternion multiplication
    Quaternion operator*(const Quaternion& other) const {
        return Quaternion(
            w * other.w - x * other.x - y * other.y - z * other.z, // w component
            w * other.x + x * other.w + y * other.z - z * other.y, // x component
            w * other.y - x * other.z + y * other.w + z * other.x, // y component
            w * other.z + x * other.y - y * other.x + z * other.w  // z component
        );
    }

    // Quaternion conjugation
    Quaternion conjugate() const {
        return Quaternion(w, -x, -y, -z);
    }

    // Print quaternion
    void print() const {
        std::cout << "Quaternion(" << w << ", " << x << ", " << y << ", " << z << ")\n";
    }
};

int main() {
    // Example usage
