#include <iostream>
#include <vector>

// Function to check if a number is prime
bool isPrime(int number) {
    if (number <= 1) return false; // 0 and 1 are not prime numbers
    for (int i = 2; i * i <= number; ++i) { // Only check up to the square root of the number
        if (number % i == 0) return false; // If divisible, it's not prime
    }
    return true; // The number is prime
}

// Function to find and return all prime numbers up to a specified limit
std::vector<int> findPrimesUpTo(int limit) {
    std::vector<int> primes;
    for (int num = 2; num <= limit; ++num) {
        if (isPrime(num)) {
            primes.push_back(num); // Add prime number to the list
        }
    }
    return primes; // Return the list of prime numbers
}

int main() {
    int limit = 50; // Define the upper limit
    std::vector<int> primes = findPrimesUpTo(limit); // Find primes up to the limit

    // Display the prime numbers
    std::cout << "Prime numbers up to " << limit << " are: ";
    for (int prime : primes) {
        std::cout << prime << " ";
    }
    std::cout << std::endl;

    return 0; // Indicate successful completion
}
```

