#include <iostream>
#include <vector>

// Module: PrimeDetector
// Responsibility: Determine whether a number is prime
class PrimeDetector {
public:
    // Focused, single-responsibility method for prime checking
    static bool isPrime(int number) {
        // Handle edge cases for numbers less than 2
        if (number < 2) return false;

        // Optimize prime check by only testing up to square root
        for (int divisor = 2; divisor * divisor <= number; ++divisor) {
            if (number % divisor == 0) return false;
        }
        return true;
    }
};

// Module: PrimeLister
// Responsibility: Generate and display list of prime numbers
class PrimeLister {
public:
    // Method to find and return all primes up to a given limit
    static std::vector<int> findPrimes(int limit) {
        std::vector<int> primes;

        // Iterate and collect prime numbers
        for (int number = 2; number <= limit; ++number) {
            if (PrimeDetector::isPrime(number)) {
                primes.push_back(number);
            }
        }

        return primes;
    }

    // Method to display prime numbers
    static void displayPrimes(const std::vector<int>& primes) {
        std::cout << "Prime numbers found: ";
        for (int prime : primes) {
            std::cout << prime << " ";
        }
        std::cout << std::endl;
    }
};

int main() {
    const int LIMIT = 50;

    // Find primes using modular, independent components
    std::vector<int> primesUpTo50 = PrimeLister::findPrimes(LIMIT);

    // Display the results
    PrimeLister::displayPrimes(primesUpTo50);

    return 0;
}

```


