// Function to count the occurrences of the digit '5' in numbers of a sequence
int five_mult_div_seq(int n, int start_range, int end_range, int m) {
    // Vector to store the ascending sequence
    std::vector<int> ascending_sequence;
    // Generate ascending sequence from start_range to n-1
    for (int num = start_range; num < n; ++num) {
        // Check if the number is divisible by 9, 14, or m
        if (num % 9 == 0 || num % 14 == 0 || num % m == 0) {
            ascending_sequence.push_back(num); // Add to ascending sequence
        }
    }

    // Vector to store the descending sequence
    std::vector<int> descending_sequence;
    // Generate descending sequence from end_range to n+1
    for (int num = end_range; num > n; --num) {
        // Check if the number is divisible by 9, 14, or m
        if (num % 9 == 0 || num % 14 == 0 || num % m == 0) {
            descending_sequence.push_back(num); // Add to descending sequence
        }
    }

    // Combine both sequences into a single vector
    std::vector<int> total_sequence;
    total_sequence.insert(total_sequence.end(), ascending_sequence.begin(), ascending_sequence.end());
    total_sequence.insert(total_sequence.end(), descending_sequence.begin(), descending_sequence.end());

    // Count the occurrences of the digit '5' in all numbers of the combined sequence
    int count = 0;
    for (int num : total_sequence) {
        std::string num_str = std::to_string(num); // Convert number to string
        for (char ch : num_str) { // Iterate through each character in the string
            if (ch == '5') {
                count++; // Increment count if the character is '5'
            }
        }
    }

