        for (int x = 0; x < maxFib; ++x) {
            double distance = std::sqrt((x - center) * (x - center) + (y - center) * (y - center));
            bool isFib = false;

            for (int fib : fibonacci) {
                if (std::abs(distance - fib) < 1.0) {
                    isFib = true;
                    break;
