        if (sequence.empty()) return;

        std::cout << "Fibonacci Ornamental Pattern:\n";
        for (size_t i = 0; i < sequence.size(); ++i) {
            renderPatternLevel(sequence[i], i);
        }
    }

private:
    // Create a symmetrical pattern level based on Fibonacci number
    void renderPatternLevel(int fibNumber, size_t level) {
        // Create symmetrical spacing and visual representation
        int padding = std::max(0, 10 - fibNumber);
        std::string symbol = determineLevelSymbol(level);

        std::cout << std::setw(padding) << "" 
                  << std::string(fibNumber, symbol[0]) 
                  << " (Fib: " << fibNumber << ")\n";
    }

    // Select visual symbol based on pattern level
    std::string determineLevelSymbol(size_t level) {
        const std::vector<std::string> symbols = {
            "*", "#", "@", "%", "&"
        };
        return symbols[level % symbols.size()];
    }
};

