#include <iostream>
#include <vector>
#include <cmath>
#include <iomanip>
#include <algorithm>

// FibonacciSequence: Responsible for generating Fibonacci numbers
class FibonacciSequence {
private:
    std::vector<long long> sequence;

public:
    // Generate Fibonacci sequence up to a specified count
    void generate(int count) {
        sequence.clear();
        sequence.push_back(0);
        sequence.push_back(1);

        while (sequence.size() < count) {
            long long next = sequence[sequence.size() - 1] + sequence[sequence.size() - 2];
            sequence.push_back(next);
        }
    }

    // Retrieve the generated sequence
    const std::vector<long long>& getSequence() const {
        return sequence;
    }
};

// PatternGenerator: Responsible for creating visual patterns
class PatternGenerator {
public:
    // Generate a spiral pattern based on Fibonacci numbers
    static void generateFibonacciSpiral(const std::vector<long long>& fibSequence, int gridSize) {
        std::vector<std::vector<char>> grid(gridSize, std::vector<char>(gridSize, ' '));

        int x = gridSize / 2;
        int y = gridSize / 2;
        int dx[] = {0, -1, 0, 1};  // Right, Up, Left, Down
        int dy[] = {1, 0, -1, 0};
        int direction = 0;

        for (size_t i = 0; i < fibSequence.size(); ++i) {
            long long steps = std::min(static_cast<long long>(std::sqrt(fibSequence[i])), static_cast<long long>(gridSize / 2));

            for (long long j = 0; j < steps; ++j) {
                if (x >= 0 && x < gridSize && y >= 0 && y < gridSize) {
                    grid[x][y] = '*';
                }
                x += dx[direction];
                y += dy[direction];
            }

            direction = (direction + 1) % 4;
        }

        // Print the pattern
        std::cout << "Fibonacci Spiral Pattern:\n";
        for (const auto& row : grid) {
            for (char cell : row) {
                std::cout << cell << ' ';
            }
            std::cout << '\n';
        }
    }
};

// Visualization: Responsible for presenting pattern details
class Visualization {
public:
    static void displayFibonacciDetails(const std::vector<long long>& fibSequence) {
        std::cout << "\nFibonacci Sequence Details:\n";
        std::cout << "------------------------\n";
        for (size_t i = 0; i < fibSequence.size(); ++i) {
            std::cout << "F(" << i << "): " << std::setw(10) << fibSequence[i] << "\n";
        }
    }
};

// Main application logic
class FibonacciPatternApp {
private:
    FibonacciSequence fibSequence;
    int sequenceLength;
    int patternGridSize;

public:
    FibonacciPatternApp(int length = 10, int gridSize = 21) 
        : sequenceLength(length), patternGridSize(gridSize) {}

    void run() {
        // Generate Fibonacci sequence
        fibSequence.generate(sequenceLength);

        // Display sequence details
        Visualization::displayFibonacciDetails(fibSequence.getSequence());

        // Generate spiral pattern
        PatternGenerator::generateFibonacciSpiral(fibSequence.getSequence(), patternGridSize);
    }
};

int main() {
    FibonacciPatternApp app(15, 41);  // 15 sequence elements, 41x41 grid
    app.run();
    return 0;
}

