import sys

def reverse_words_preserve_punctuation(sentence):
    """Reverse each word while preserving punctuation and word order."""
    words = []
    current_word = []
    for char in sentence:
        if char.isalpha():
            current_word.append(char)
        else:
            if current_word:
                words.append(''.join(current_word))
                current_word = []
            words.append(char)
    if current_word:
        words.append(''.join(current_word))

    reversed_words = []
    for item in words:
        if item.isalpha():
            reversed_words.append(item[::-1])
        else:
            reversed_words.append(item)

    return ''.join(reversed_words)

def count_vowels(word):
    """Count the number of vowels in a word (case-insensitive)."""
    vowels = {'a', 'e', 'i', 'o', 'u'}
    return sum(1 for char in word.lower() if char in vowels)

def process_sentence(sentence):
    """Process a sentence to reverse words and count vowels."""
    # Reverse the words while preserving punctuation
    reversed_sentence = reverse_words_preserve_punctuation(sentence)
    print(f"Reversed sentence: {reversed_sentence}")

    # Extract words (without punctuation) and count vowels
    words = [word.lower() for word in sentence.split() if word.isalpha()]
    print("Vowel counts per word:")
    for word in words:
        print(f"'{word}': {count_vowels(word)} vowels")

def main():
    """Main function to handle input from command line or user input."""
    if len(sys.argv) > 1:
        # Get sentence from command line arguments
        sentence = ' '.join(sys.argv[1:])
        print(f"Processing command line input: {sentence}")
        process_sentence(sentence)
    else:
        # Get sentence from user input
        while True:
            print("\nEnter a sentence to process (or 'quit' to exit):")
            sentence = input().strip()
            if sentence.lower() == 'quit':
                break
            if sentence:
                process_sentence(sentence)
            else:
                print("Please enter a valid sentence.")

if __name__ == "__main__":
    main()
```

