#include <vector>
#include <iomanip>
#include <stdexcept>

class FirstYearStudentPortfolio {
private:
    std::string fullName;
    std::string academicID;
    std::vector<double> courseGrades;

    // Helper function to validate GPA input
    bool isValidGrade(double grade) const {
        return grade >= 0.0 && grade <= 4.0;
    }

public:
    // Constructor
    FirstYearStudentPortfolio(const std::string& name, const std::string& id) 
        : fullName(name), academicID(id) {
        if (id.empty()) {
            throw std::invalid_argument("Academic ID cannot be empty");
        }
    }

    // Add a course grade to the portfolio
    void addCourseGrade(double grade) {
        if (!isValidGrade(grade)) {
            throw std::invalid_argument("Grade must be between 0.0 and 4.0");
        }
        courseGrades.push_back(grade);
    }

    // Calculate GPA
    double calculateGPA() const {
        if (courseGrades.empty()) {
            return 0.0;
        }

        double sum = 0.0;
        for (double grade : courseGrades) {
            sum += grade;
        }
        return sum / courseGrades.size();
    }

    // Getter methods
    std::string getFullName() const { return fullName; }
    std::string getAcademicID() const { return academicID; }
    std::vector<double> getCourseGrades() const { return courseGrades; }

    // Display student portfolio
    void displayPortfolio() const {
        std::cout << "\nSTUDENT PORTFOLIO SUMMARY\n";
        std::cout << "-------------------------\n";
        std::cout << "Full Name: " << fullName << "\n";
        std::cout << "Academic ID: " << academicID << "\n";
        std::cout << "Course Grades: ";

        for (double grade : courseGrades) {
            std::cout << std::fixed << std::setprecision(2) << grade << " ";
        }

        std::cout << "\nGPA: " << std::fixed << std::setprecision(2) << calculateGPA() << "\n";
        std::cout << "-------------------------\n";
    }
};

int main() {
    try {
        // Create a student portfolio
        FirstYearStudentPortfolio student("John William Smith", "U2023-12345");

        // Add some course grades
        student.addCourseGrade(3.5);
        student.addCourseGrade(3.0);
        student.addCourseGrade(4.0);
        student.addCourseGrade(3.7);

        // Display the portfolio
        student.displayPortfolio();
    }
    catch (const std::exception& e) {
        std::cerr << "Error: " << e.what() << std::endl;
        return 1;
    }

    return 0;
}
