import java.util.concurrent.locks.ReentrantLock;

class Node {
    String id;
    int value;
    Node next;

    Node(String id, int value) {
        this.id = id;
        this.value = value;
        this.next = null;
    }
}

public class CircularLinkedList {
    private Node head;
    private final ReentrantLock lock = new ReentrantLock();

    public CircularLinkedList() {
        this.head = null;
    }

    // Insert a new node
    public void insert(String id, int value) {
        lock.lock();
        try {
            Node newNode = new Node(id, value);
            if (head == null) {
                head = newNode;
                head.next = head; // Circular link
            } else {
                Node current = head;
                while (current.next != head) {
                    current = current.next;
                }
                current.next = newNode;
                newNode.next = head; // Maintain circular structure
            }
        } finally {
            lock.unlock();
        }
    }

    // Delete a node by unique identifier
    public boolean delete(String id) {
        lock.lock();
        try {
            if (head == null) return false;
            Node current = head;
            Node previous = null;

            do {
                if (current.id.equals(id)) {
                    if (previous == null) {
                        // Deleting head node
                        if (current.next == head) {
                            head = null; // List becomes empty
                        } else {
                            Node tail = head;
                            while (tail.next != head) {
                                tail = tail.next;
                            }
                            head = current.next; // Update head
                            tail.next = head; // Update the tail's next
                        }
                    } else {
                        previous.next = current.next; // Bypass the node
                    }
                    return true; // Node deleted
                }
                previous = current;
                current = current.next;
            } while (current != head);
            return false; // Node not found
        } finally {
            lock.unlock();
        }
    }

    // Search for a node by unique identifier
    public Integer search(String id) {
        lock.lock();
        try {
            if (head == null) return null;
            Node current = head;
            do {
                if (current.id.equals(id)) {
                    return current.value; // Return the value if found
                }
                current = current.next;
            } while (current != head);
            return null; // Node not found
        } finally {
            lock.unlock();
        }
    }

    // Method to display the list (for testing purposes)
    public void display() {
        lock.lock();
        try {
            if (head == null) return;
            Node current = head;
            do {
                System.out.println("ID: " + current.id + ", Value: " + current.value);
                current = current.next;
            } while (current != head);
        } finally {
            lock.unlock();
        }
    }

    public static void main(String[] args) {
