import java.util.concurrent.locks.ReentrantLock;

public class CircularLinkedList {
    private static class Node {
        final String id;
        final int value;
        Node next;
        final ReentrantLock lock = new ReentrantLock();

        Node(String id, int value) {
            this.id = id;
            this.value = value;
            this.next = this; // circular by default
        }
    }

    private Node head;
    private final ReentrantLock listLock = new ReentrantLock();
    private final int capacity;
    private int size;

    public CircularLinkedList(int capacity) {
        if (capacity <= 0) {
            throw new IllegalArgumentException("Capacity must be positive");
        }
        this.capacity = capacity;
        this.size = 0;
    }

    public boolean insert(String id, int value) {
        listLock.lock();
        try {
            if (size >= capacity) {
                return false;
            }
            if (head == null) {
                head = new Node(id, value);
                size++;
                return true;
            }

            Node current = head;
            current.lock.lock();
            try {
                while (current.next != head) {
                    Node next = current.next;
                    next.lock.lock();
                    try {
                        current.lock.unlock();
                        current = next;
                    } catch (Exception e) {
                        next.lock.unlock();
                        throw e;
                    }
                }

                Node newNode = new Node(id, value);
                newNode.next = head;
                current.next = newNode;
                size++;
                return true;
            } finally {
                current.lock.unlock();
            }
        } finally {
            listLock.unlock();
        }
    }

    public boolean delete(String id) {
        listLock.lock();
        try {
            if (head == null) {
                return false;
            }

            Node prev = null;
            Node current = head;
            current.lock.lock();
            try {
                do {
                    if (current.id.equals(id)) {
                        if (prev == null) { // deleting head
                            if (size == 1) {
                                head = null;
                            } else {
                                Node last = head;
                                while (last.next != head) {
                                    last = last.next;
                                }
                                last.next = head.next;
                                head = head.next;
                            }
                        } else {
                            prev.next = current.next;
                            if (current == head) {
                                head = current.next;
                            }
                        }
                        size--;
                        return true;
                    }

                    Node next = current.next;
                    next.lock.lock();
                    try {
                        if (prev != null) {
                            prev.lock.unlock();
                        }
                        prev = current;
                        current = next;
                    } catch (Exception e) {
                        next.lock.unlock();
                        throw e;
                    }
                } while (current != head);

                return false;
            } finally {
                current.lock.unlock();
                if (prev != null && prev != current) {
                    prev.lock.unlock();
                }
            }
        } finally {
            listLock.unlock();
        }
    }

    public Integer search(String id) {
        listLock.lock();
        try {
            if (head == null) {
                return null;
            }

            Node current = head;
            current.lock.lock();
            try {
                do {
                    if (current.id.equals(id)) {
                        return current.value;
                    }
                    Node next = current.next;
                    next.lock.lock();
                    try {
                        current.lock.unlock();
                        current = next;
                    } catch (Exception e) {
                        next.lock.unlock();
                        throw e;
                    }
                } while (current != head);

                return null;
            } finally {
                current.lock.unlock();
            }
        } finally {
            listLock.unlock();
        }
    }

