import java.util.concurrent.locks.ReentrantLock;

public class ThreadSafeCircularLinkedList<K, V> {
    // Node class representing each element in the circular linked list
    private class Node {
        K identifier;
        V value;
        Node next;

        Node(K identifier, V value) {
            this.identifier = identifier;
            this.value = value;
        }
    }

    // Head of the circular linked list
    private Node head;

    // Maximum capacity of the list
    private final int capacity;

    // Thread synchronization lock
    private final ReentrantLock lock;

    // Current size of the list
    private int size;

    // Constructor
    public ThreadSafeCircularLinkedList(int capacity) {
        if (capacity <= 0) {
            throw new IllegalArgumentException("Capacity must be positive");
        }
        this.capacity = capacity;
        this.lock = new ReentrantLock();
        this.size = 0;
    }

    // Method to insert a new node
    public boolean insert(K identifier, V value) {
        lock.lock();
        try {
            // Check if list is at capacity
            if (size >= capacity) {
                return false;
            }

            // Check for duplicate identifier
            if (findNode(identifier) != null) {
                return false;
            }

            Node newNode = new Node(identifier, value);

            // If list is empty
            if (head == null) {
                head = newNode;
                head.next = head;
            } else {
                // Insert at the end
                Node current = head;
                while (current.next != head) {
                    current = current.next;
                }
                current.next = newNode;
                newNode.next = head;
            }

            size++;
            return true;
        } finally {
            lock.unlock();
        }
    }

    // Method to delete a node by identifier
    public boolean delete(K identifier) {
        lock.lock();
        try {
            if (head == null) {
                return false;
            }

            // Special case for single node
            if (head.identifier.equals(identifier) && head.next == head) {
                head = null;
                size--;
                return true;
            }

            Node current = head;
            Node prev = null;

            do {
                if (current.identifier.equals(identifier)) {
                    // Found the node to delete
                    if (prev != null) {
                        prev.next = current.next;
                        if (current == head) {
                            head = current.next;
                        }
                    } else {
                        // Deleting head node
                        Node last = head;
                        while (last.next != head) {
                            last = last.next;
                        }
                        last.next = current.next;
                        head = current.next;
                    }
                    size--;
                    return true;
                }
                prev = current;
                current = current.next;
            } while (current != head);

            return false;
        } finally {
            lock.unlock();
        }
    }

    // Method to find a node by identifier
    public V search(K identifier) {
        lock.lock();
        try {
            Node foundNode = findNode(identifier);
            return foundNode != null ? foundNode.value : null;
        } finally {
            lock.unlock();
        }
    }

    // Internal method to find a node by identifier
    private Node findNode(K identifier) {
        if (head == null) {
            return null;
        }

        Node current = head;
        do {
            if (current.identifier.equals(identifier)) {
                return current;
            }
            current = current.next;
        } while (current != head);

        return null;
    }

    // Get current size of the list
    public int size() {
        lock.lock();
        try {
            return size;
        } finally {
            lock.unlock();
        }
    }

    // Main method for demonstration
    public static void main(String[] args) {
        ThreadSafeCircularLinkedList<String, Integer> list = 
            new ThreadSafeCircularLinkedList<>(5);

        // Demonstrate thread-safe operations
        list.insert("A", 1);
        list.insert("B", 2);
        list.insert("C", 3);

        System.out.println("Size: " + list.size());
        System.out.println("Search B: " + list.search("B"));

        list.delete("B");
        System.out.println("Size after deletion: " + list.size());
    }
}
