            this.identifier = identifier;
            this.value = value;
        }
    }

    // Thread-safe synchronization mechanism
    private final ReentrantLock lock = new ReentrantLock();
    private Node head;
    private int size;
    private final int maxCapacity;

    // Constructor with configurable capacity
    public ThreadSafeCircularLinkedList(int maxCapacity) {
        if (maxCapacity <= 0) {
            throw new IllegalArgumentException("Capacity must be positive");
        }
        this.maxCapacity = maxCapacity;
        this.size = 0;
    }

    // Thread-safe insertion method
    public boolean insert(K identifier, V value) {
        lock.lock();
        try {
            // Check for duplicate identifiers and capacity
            if (search(identifier) != null || size >= maxCapacity) {
                return false;
            }

            Node newNode = new Node(identifier, value);

            // First node insertion
            if (head == null) {
                head = newNode;
                head.next = head;
                head.prev = head;
            } else {
                // Insert at the end of the list
                Node last = head.prev;
                newNode.next = head;
                newNode.prev = last;
                last.next = newNode;
                head.prev = newNode;
            }

            size++;
            return true;
        } finally {
            lock.unlock();
        }
    }

    // Thread-safe deletion method
    public boolean delete(K identifier) {
        lock.lock();
        try {
            Node nodeToDelete = search(identifier);
            if (nodeToDelete == null) {
                return false;
            }

            // Single node case
            if (size == 1) {
                head = null;
            } else {
                // Multiple nodes case
                nodeToDelete.prev.next = nodeToDelete.next;
                nodeToDelete.next.prev = nodeToDelete.prev;

                // Update head if needed
                if (nodeToDelete == head) {
                    head = nodeToDelete.next;
                }
            }

            size--;
            return true;
        } finally {
            lock.unlock();
        }
    }

    // Thread-safe search method
    public V search(K identifier) {
        lock.lock();
        try {
            if (head == null) {
                return null;
            }

            Node current = head;
            do {
                if (current.identifier.equals(identifier)) {
                    return current.value;
                }
                current = current.next;
            } while (current != head);

            return null;
        } finally {
            lock.unlock();
        }
    }

