    static void sortRecursive(std::vector<int>& arr) {
        if (arr.empty()) return;
        quickSortRecursive(arr, 0, arr.size() - 1);
    }

    // Iterative Quicksort public method
    static void sortIterative(std::vector<int>& arr) {
        if (arr.empty()) return;
        quickSortIterative(arr, 0, arr.size() - 1);
    }

