import java.util.*;

public static String[] findAnagrams(String[] lexemes) {
    if (lexemes == null || lexemes.length == 0) {
        return new String[0];
    }

    Map<String, List<String>> anagramGroups = new HashMap<>();

    for (String lexeme : lexemes) {
        if (lexeme == null || lexeme.length() < 2) {
            continue;
        }

        char[] chars = lexeme.toCharArray();
        Arrays.sort(chars);
        String sorted = new String(chars);

        if (!anagramGroups.containsKey(sorted)) {
            anagramGroups.put(sorted, new ArrayList<>());
        }
        anagramGroups.get(sorted).add(lexeme);
    }

    // Find the largest group of anagrams
    List<String> largestGroup = new ArrayList<>();
    for (List<String> group : anagramGroups.values()) {
        if (group.size() > largestGroup.size()) {
            largestGroup = group;
        }
    }

    return largestGroup.toArray(new String[0]);
}
