```python
class Animal:
    def __init__(self, species, habitat, diet, lifespan):
        self._species = species
        self._habitat = habitat
        self._diet = diet
        self._lifespan = lifespan

    @property
    def species(self):
        return self._species

    @property
    def habitat(self):
        return self._habitat

    @property
    def diet(self):
        return self._diet

    @property
    def lifespan(self):
        return self._lifespan

    def sound(self):
        raise NotImplementedError("Subclasses should implement this method")

    def display_info(self):
        return f"{self.species}: Habitat: {self.habitat}, Diet: {self.diet}, Lifespan: {self.lifespan} years"


class Lion(Animal):
    def __init__(self, pride):
        super().__init__(species='Lion', habitat='Savannah', diet='Carnivore', lifespan=12)
        self._pride = pride

    @property
    def pride(self):
        return self._pride

    def sound(self):
        return "Roar"

    def hunt(self):
        return "The lion hunts for prey."

    def display_info(self):
        return super().display_info() + f", Pride size: {self.pride}"


class Elephant(Animal):
    def __init__(self, herd):
        super().__init__(species='Elephant', habitat='Grasslands', diet='Herbivore', lifespan=60)
        self._herd = herd

    @property
    def herd(self):
        return self._herd

    def sound(self):
        return "Trumpet"

    def trumpet(self):
        return "The elephant trumpets loudly."

    def display_info(self):
        return super().display_info() + f", Herd size: {self.herd}"


class Eagle(Animal):
    def __init__(self, nest):
        super().__init__(species='Eagle', habitat='Mountains', diet='Carnivore', lifespan=20)
        self._nest = nest

    @property
    def nest(self):
        return self._nest

    def sound(self):
        return "Screech"

    def soar(self):
        return "The eagle soars high in the sky."

    def display_info(self):
        return super().display_info() + f", Nest location: {self.nest}"


class Habitat:
    def __init__(self):
        self._animals = []

    def add_animal(self, animal):
        if not isinstance(animal, Animal):
            raise TypeError("Only Animal instances can be added to the habitat.")
        self._animals.append(animal)

