```java
import java.util.HashMap;
import java.util.Map;

public class MainClass {
    public static void main(String[] args) {
        Object[] designatedSet = {1, 2, 3, 2, 'a', 4, 5, 2, 4, 5, 5};
        try {
            Map<Integer, Integer> counts = countRecurrentEntities(designatedSet);
            displayRecurrentEntities(counts);
        } catch (IllegalArgumentException e) {
            System.err.println("Error: " + e.getMessage());
        }
    }

    static Map<Integer, Integer> countRecurrentEntities(Object[] arr) throws IllegalArgumentException {
        Map<Integer, Integer> countMap = new HashMap<>();

        for (Object element : arr) {
            if (!(element instanceof Integer)) {
                if (element instanceof Character) {
                    throw new IllegalArgumentException("Character '" + element + "' found. Only integers are allowed.");
                } else {
                    throw new IllegalArgumentException("Invalid data type found: " + element.getClass().getSimpleName());
                }
            }

            int num = (Integer) element;
            countMap.put(num, countMap.getOrDefault(num, 0) + 1);
        }

        return countMap;
    }

    static void displayRecurrentEntities(Map<Integer, Integer> counts) {
        for (Map.Entry<Integer, Integer> entry : counts.entrySet()) {
            if (entry.getValue() > 1) {
                System.out.println(entry.getKey() + " appears " + entry.getValue() + " times");
            }
        }
    }
}
```

