import java.util.*;

public class RecurrentEntityCounter {
    public static void main(String[] args) {
        try {
            int[] designatedSet = {1, 2, 3, 2, 4, 5, 2, 4, 5, 5};
            Map<Integer, Integer> recurrentEntities = countRecurrentEntities(designatedSet);
            displayRecurrentEntities(recurrentEntities);
